SELECT `spree_variants`.* FROM `spree_variants` WHERE `spree_variants`.`deleted_at` IS NULL AND `spree_variants`.`product_id` = 1 AND `spree_variants`.`is_master` = FALSE AND EXISTS (SELECT `spree_prices`.* FROM `spree_prices` WHERE `spree_prices`.`deleted_at` IS NULL AND `spree_variants`.`id` = `spree_prices`.`variant_id` AND (spree_prices.currency = 'USD' AND (spree_prices.country_iso IS NULL OR spree_prices.country_iso = NULL))) ORDER BY `spree_variants`.`position` ASC;
